html:
{%- from "templates/print_formats/standard_macros.html" import add_header,
	 render_field -%}
	<div id="header-html" class="hidden-pdf">
               {{ add_header(0,1,doc,letter_head, no_letterhead) }}
	</div>

	<div id="footer-html" class="visible-pdf">
		{% if not no_letterhead and footer %}
		<div class="letter-head-footer">
			{{ footer }}
		</div>
		{% endif %}
		<p class="text-center small page-number visible-pdf">
			{{ _("Page {0} of {1}").format('<span class="page"></span>', '<span class="topage"></span>') }}
		</p>
	</div>



<style>
table {
  border-collapse: collapse;
  width: 100%;

}

td, th {
  border: 1px solid #dddddd;
  text-align: center;
  border-spacing:30px; 
}
</style>

<table>
    <tr>
      <td style="text-align:left; border: #dddddd"><b>{{doc.name1}}</b><br><br><b>{{doc.address}}</b><br><br><b>+91 {{doc.phone_number}}</b></td>
      <td style="text-align:right; border: #dddddd"><b>{{doc.date}}</b><br><br><b>{{doc.day}}</b></td>
    </tr>
</table>


<p>
  <br> <br>
</p>
 <center><table border = "1">
    <tr>
        <th>Item Number</th>
        <th>Item Name</th>
        <th>Item Quantity</th>
        <th>Item Price</th>
        <th>Total Price</th>
    </tr>

    {%- for row in doc.item_details -%}
    <tr>
    <td>{{ row.item_code }}</td>
    <td>{{ row.item_name }}</td>
    <td>{{ row.item_quantity }}</td>
    <td>{{ row.item_cost}}</td>
    <td>{{ row.item_cost * row.item_quantity}}</td>
    </tr>
    {%- endfor -%} 
      </table>
    </center>

</body>
